    fun setupLocalization(){
        val myLocale = Locale(mUtils.getLangSettingFromSharePre(context,"fr"))
        val res: Resources = getResources()
        val dm = res.displayMetrics
        val conf = res.configuration
        conf.locale = myLocale
        res.updateConfiguration(conf, dm)
    }
    fun launchActivity() {
        Handler().postDelayed({
            var i: Intent
            if (SharedPref.read(Constants.LOGIN,false)) {
                i = Intent(this, DashboardActivity::class.java)
                startActivity(i)
                finish()
            } else {
                i = Intent(this, LoginActivity::class.java)
                startActivity(i)
                finish()
            }
        },SPLASH_TIMEOUT.toLong())
    }

    fun acessRuntimPermission() {
        val permissionListener: PermissionListener = object : PermissionListener {
            override fun onPermissionGranted() {
                launchActivity()
            }

            override fun onPermissionDenied(deniedPermissions: ArrayList<String>) {

            }
        }
        TedPermission.with(this)
            .setPermissionListener(permissionListener)
            .setRationaleMessage(getString(R.string.Please_give_permission_for_app_functionality))
            .setDeniedMessage(
                getString(R.string.If_you_reject_permission_you_can_not_use_this_service) + "\n\n" + getString(
                    R.string.Please_turn_on_permissions_at
                )
            )
            .setGotoSettingButtonText("setting")
            .setPermissions(
                Manifest.permission.INTERNET,
                Manifest.permission.ACCESS_NETWORK_STATE,
                Manifest.permission.ACCESS_FINE_LOCATION,
                Manifest.permission.ACCESS_COARSE_LOCATION,
                Manifest.permission.BLUETOOTH,
                Manifest.permission.BLUETOOTH_ADMIN,
                Manifest.permission.CAMERA,
                Manifest.permission.READ_EXTERNAL_STORAGE,
                Manifest.permission.WRITE_EXTERNAL_STORAGE
            )
            .check()
    }


